import React, { useState, useEffect } from 'react';
import { getAllResources, classifyResources, getStats } from '../api/resourceApi';
import AnalyticsDashboard from './AnalyticsDashboard';
import DynamicClassifier from './DynamicClassifier';
import './ResourceTable.css';

/**
 * Component hi·ªÉn th·ªã b·∫£ng t√†i nguy√™n v√† ph√¢n lo·∫°i
 */
function ResourceTable() {
  const [resources, setResources] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState(null);
  const [classified, setClassified] = useState(false);

  // Load d·ªØ li·ªáu khi component mount
  useEffect(() => {
    loadResources();
  }, []);

  /**
   * Load to√†n b·ªô t√†i nguy√™n
   */
  const loadResources = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getAllResources();
      setResources(data);
      setClassified(false);
      setStats(null);
    } catch (err) {
      setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu. H√£y ch·∫Øc ch·∫Øn Backend ƒëang ch·∫°y!');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  /**
   * Ph√¢n lo·∫°i to√†n b·ªô t√†i nguy√™n
   */
  const handleClassify = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await classifyResources();
      console.log('Classify response:', response);
      
      // Backend tr·∫£ v·ªÅ: {success, message, totalResources, resources}
      if (response.resources && Array.isArray(response.resources)) {
        setResources(response.resources);
        setClassified(true);
        
        // T√≠nh stats t·ª´ classified resources
        const totalResources = response.resources.length;
        const classifiedCount = response.resources.filter(r => r.classification && r.classification !== 'Not Classified').length;
        
        setStats({
          totalResources,
          classified: classifiedCount,
          unclassified: totalResources - classifiedCount
        });
      } else {
        setError('Backend tr·∫£ v·ªÅ d·ªØ li·ªáu kh√¥ng ƒë√∫ng ƒë·ªãnh d·∫°ng');
      }
    } catch (err) {
      setError('Kh√¥ng th·ªÉ ph√¢n lo·∫°i. Ki·ªÉm tra Backend!');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  /**
   * L·∫•y m√†u badge theo classification
   */
  const getClassificationBadge = (classification) => {
    if (!classification || classification === 'Not Classified') {
      return <span className="badge badge-gray">Ch∆∞a ph√¢n lo·∫°i</span>;
    }

    let colorClass = 'badge-default';
    
    if (classification.includes('Production')) {
      colorClass = 'badge-red';
    } else if (classification.includes('Testing')) {
      colorClass = 'badge-blue';
    } else if (classification.includes('Staging')) {
      colorClass = 'badge-yellow';
    }

    if (classification.includes('Critical')) {
      colorClass = 'badge-red';
    }

    return <span className={`badge ${colorClass}`}>{classification}</span>;
  };

  /**
   * L·∫•y icon theo service type
   */
  const getServiceIcon = (classification) => {
    if (!classification) return 'üì¶';
    if (classification.includes('Database')) return 'üóÑÔ∏è';
    if (classification.includes('Caching')) return '‚ö°';
    if (classification.includes('Compute')) return 'üíª';
    if (classification.includes('Storage')) return 'üíæ';
    if (classification.includes('Network')) return 'üåê';
    return 'üì¶';
  };

  return (
    <div className="resource-container">
      {/* Action Buttons */}
      <div className="action-bar">
        <button 
          className="btn btn-primary" 
          onClick={loadResources}
          disabled={loading}
        >
          {loading ? '‚è≥ ƒêang t·∫£i...' : 'üîÑ Load Data'}
        </button>
        
        <button 
          className="btn btn-success" 
          onClick={handleClassify}
          disabled={loading || resources.length === 0}
        >
          {loading ? '‚è≥ ƒêang x·ª≠ l√Ω...' : 'üéØ Classify Resources'}
        </button>

        <div className="info-badge">
          üìä T·ªïng: <strong>{resources.length}</strong> t√†i nguy√™n
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <div className="alert alert-error">
          ‚ùå {error}
        </div>
      )}

      {/* Stats Panel */}
      {classified && stats && (
        <div className="stats-panel">
          <h3>üìà Th·ªëng k√™ ph√¢n lo·∫°i</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-label">T·ªïng s·ªë t√†i nguy√™n</div>
              <div className="stat-value">{stats.totalResources}</div>
            </div>
            <div className="stat-card">
              <div className="stat-label">Production</div>
              <div className="stat-value prod">{stats.environmentCounts?.Production || 0}</div>
            </div>
            <div className="stat-card">
              <div className="stat-label">Testing</div>
              <div className="stat-value test">{stats.environmentCounts?.Testing || 0}</div>
            </div>
            <div className="stat-card">
              <div className="stat-label">Critical Resources</div>
              <div className="stat-value critical">{stats.criticalCount || 0}</div>
            </div>
          </div>
        </div>
      )}

      {/* Resource Table */}
      {resources.length > 0 ? (
        <div className="table-wrapper">
          <table className="resource-table">
            <thead>
              <tr>
                <th>üÜî ID</th>
                <th>üìõ Name</th>
                <th>‚öôÔ∏è CPU (vCPU)</th>
